import React from 'react'
  

const ContactForm = () => { 
        return (
            <form className='myForm' onSubmit={event => this.handleSubmit(event)} >
              <fieldset className='dob-input-container' style={{border: "5px"}}>
                
                    <div className='form-item'>
                      <label htmlFor="name-input">Name<span className='req'>*</span></label>
                      <input  type='text' 
                              id='name-input'
                              className='name-input'
                              placeholder='ex: Arion Askins'
                              minLength={3}
                              onChange={event => this.handleNameChange(event)}
                              value={this.state.name} 
                              
                              required />
                    </div>

                    <div className='form-item'>
                      <label htmlFor="email-input">Email<span className='req'>*</span></label>
                      <input type='text' 
                              placeholder='ex: askinsarion@gmail.com'
                              minLength={3}
                              value={this.formData.email} 
                              onChange={event => this.handleEmailChange(event)}
                              className='email-input'
                              id='email-input' 
                              required />
                    </div>

                    <div className='form-item'>
                      <label htmlFor="phone-input">Phone</label>
                      <input type='tel' 
                              placeholder='XXX-XXX-XXXX'
                              minLength={10}
                              value={this.formData.phone} 
                              onChange={event => this.handlePhoneChange(event)}
                              className='phone-input'
                              id='phone-input' />
                    </div>

                    <div className='form-item'>
                      <label htmlFor="textarea">Message</label>
                      <textarea className='form-item'
                                rows={5}
                                value={this.formData.message} 
                                cols={5} />
                    </div>

              </fieldset>

            <div>
             <button className='btn' id='submit' type="submit">SUBMIT</button> 
            </div>      
        </form>
        )
      }

export default ContactForm;
